package exmref;
import java.util.*;
public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int num;
		System.out.println("Enter the number");
		num=sc.nextInt();
		if(num%2==0)
		{
			System.out.println("Even");
		}
		else
		{
		    System.out.println("odd");
		}
	}

}
